mapper.select:
select * from ( SELECT p.*, u.username salesName, u1.username managerName, (SELECT SUM(amount * take_time) FROM project_task WHERE task_relation_id IN (SELECT task_relation_id FROM project_task_relation WHERE project_id = p.project_id) AND `status` = 0 AND task_status = "00") takeTime, ( SELECT GROUP_CONCAT( u.username ) FROM project_user pu LEFT JOIN `user` u ON pu.user_id = u.user_id WHERE pu.type = 3 AND pu.project_id = p.project_id ) userNames FROM project p LEFT JOIN project_user pu ON p.project_id = pu.project_id AND pu.type = 1 LEFT JOIN `user` u ON pu.user_id = u.user_id LEFT JOIN project_user pu1 ON p.project_id = pu1.project_id AND pu1.type = 2 LEFT JOIN `user` u1 ON pu1.user_id = u1.user_id
where p.project_id in
#{id}
) a where 1=1
and a.code LIKE "%${projectNo}%"
and a.name LIKE "%${projectName}%"
and a.auditing_state = 1
and a.auditing_state <> 1
and a.state =#{state}
and a.sale_commis_state =#{saleCommisState}
and a.settlement_state =#{settlementState}
and a.managerName LIKE "%${managerName}%"
and a.salesName LIKE "%${salesName}%"
and a.userNames LIKE "%${userNames}%"
and a.ctime > #{startDate} and a.ctime < #{endDate}
order by a.project_id desc
LIMIT ${offset} , ${limit}

select count(*) from ( SELECT p.*, u.username salesName, u1.username managerName, (SELECT SUM(amount * take_time) FROM project_task WHERE task_relation_id IN (SELECT task_relation_id FROM project_task_relation WHERE project_id = p.project_id) AND `status` = 0 AND task_status = "00") takeTime, ( SELECT GROUP_CONCAT( u.username ) FROM project_user pu LEFT JOIN `user` u ON pu.user_id = u.user_id WHERE pu.type = 3 AND pu.project_id = p.project_id ) userNames FROM project p LEFT JOIN project_user pu ON p.project_id = pu.project_id AND pu.type = 1 LEFT JOIN `user` u ON pu.user_id = u.user_id LEFT JOIN project_user pu1 ON p.project_id = pu1.project_id AND pu1.type = 2 LEFT JOIN `user` u1 ON pu1.user_id = u1.user_id
where p.project_id in
#{id}
) a where 1=1
and a.code LIKE "%${projectNo}%"
and a.name LIKE "%${projectName}%"
and a.auditing_state = 1
and a.auditing_state <> 1
and a.state =#{state}
and a.sale_commis_state =#{saleCommisState}
and a.settlement_state =#{settlementState}
and a.managerName LIKE "%${managerName}%"
and a.salesName LIKE "%${salesName}%"
and a.userNames LIKE "%${userNames}%"
and a.ctime > #{startDate} and a.ctime < #{endDate}
order by a.project_id desc

SELECT p.project_id, p.name,p.code , p.fullname, u.username managerName, p.estincome, (SELECT SUM(settlement_income) FROM settlement WHERE project_id = p.project_id and state = 1 ) settlementIncome, (SELECT SUM(re.cny_money) FROM revenue re LEFT JOIN receivement r on re.receivement_id = r.id WHERE r.receivement_type_id in (1,2,3) and re.project_id = p.project_id) relRevenue, (SELECT SUM(cny_money) FROM invoice WHERE project_id = p.project_id) receivable from project p LEFT JOIN project_data_source pds USING(project_id) LEFT JOIN data_source ds USING(data_source_id) LEFT JOIN project_user pu ON pu.project_id = p.project_id AND type = 2 LEFT JOIN `user` u USING(user_id) where 1=1
and p.state = #{state}
and pds.data_source_id = #{dataSourceId}
and (p.name like "%${keyWord}%" or p.code like "%${keyWord}%" or ds.data_source_name like "%${keyWord}%" or u.username like "%${keyWord}%")
and p.create_datetime > #{startDate} and p.create_datetime < #{endDate}
order by p.project_id desc
