mapper.select:
select * from project_task pt
left join project_task_relation ptr
using (task_relation_id)
where 1=1

    ptr.project_id = #{projectTaskEntity.projectId}


    ptr.template_id = #{projectTaskEntity.relationTemplateId}


    pt.user_id = #{projectTaskEntity.userId}


    pt.status = #{projectTaskEntity.status}


    pt.task_status = #{projectTaskEntity.taskStatus}


    limit ${offset} , ${limit}
