mapper.select:
select * from project_task pt
left join project_task_relation ptr
using (task_relation_id)
where 1=1

    ptr.project_id = #{projectTaskEntity.projectId}


    ptr.template_id = #{projectTaskEntity.relationTemplateId}


    pt.user_id = #{projectTaskEntity.userId}


    pt.status = #{projectTaskEntity.status}


    pt.task_status = #{projectTaskEntity.taskStatus}


    limit ${offset} , ${limit}

select p.name projectName, u.username, pt.amount, pt.take_time, t.template_name, ptr.template_name relationName, pt.completion_time
from project_task pt
left join project_task_relation ptr
using (task_relation_id)
left join template t on pt.template_id = t.template_id
left join `user` u using (user_id)
left join project p using (project_id)
where 1=1
and pt.status  <>  -1

    and p.project_id = #{projectId}


    and u.user_id = #{userId}


    and ptr.task_relation_id = #{taskRelationId}
